let bbcode_action=function(){$(document).on("click","[data-action='bbcode_smileys']",function(e){e.preventDefault();let bbcode=$(this).data("smiley"),editor=$(this).closest(".panel-txtarea").find("textarea");if(editor.length===1){let curPos=editor[0].selectionStart,curVal=$(editor).val();$(editor).val(curVal.slice(0,curPos)+bbcode+curVal.slice(curPos))}})};window.onload=bbcode_action();